    private void okButActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_okButActionPerformed
        new Thread(){
            public void run(){
                Game.setP1Wins(0);
                Game.setP2Wins(0);
                TicTacGame.newGame(false); 
            }
        }.start(); 
        this.dispose();
    }//GEN-LAST:event_okButActionPerformed

